mapper.select:
select
  hb.ID boothId, hb.BOOTH_NAME boothName , hb.BOOTH_NAME boothName , hb.BOOTH_ALIAS_NAME boothAliasName,hb.BOOTH_TYPE boothType,
hbcr.CHANNEL_ID channelId
from
  hs_booth hb , hs_booth_channel_rel hbcr

    hb.ID = hbcr.BOOTH_ID

      and hb.LANGUAGE_VERSION = #{languageVersion}



      and hb.BOOTH_NAME = #{boothName}


      and hb.BOOTH_ALIAS_NAME = #{boothAliasName}


      and hb.BOOTH_TYPE = #{boothType}


      and hb.BOOTH_STATE = #{boothState}


      and hb.IS_DEL = #{isDel}

select
  hb.ID boothId , hb.BOOTH_ALIAS_NAME boothAliasName,hbcr.CHANNEL_ID channelId
from
hs_booth hb , hs_booth_channel_rel hbcr,hs_channel hc

  hb.ID = hbcr.BOOTH_ID and hbcr.CHANNEL_ID =hc.ID

    and hb.LANGUAGE_VERSION = #{languageVersion}


    and hb.BOOTH_STATE = #{boothState}


    and hc.CHANNEL_ALIAS_NAME = #{channelAliasName}


    and hc.CHANNEL_STATE = #{channelState}


    and hb.IS_DEL = #{isDel} and hc.IS_DEL = #{isDel}

select
hb.ID boothId , hb.BOOTH_ALIAS_NAME boothAliasName,hb.BOOTH_TYPE boothType
from
hs_booth hb


    and hb.LANGUAGE_VERSION = #{languageVersion}


    and hb.BOOTH_ALIAS_NAME in
    #{item}


    and hb.BOOTH_STATE = #{boothState}


    and hb.IS_DEL = #{isDel}

SELECT
${item}
FROM hs_booth



    ORDER BY #{orderBy}


    ORDER BY CREATE_TIME DESC
