mapper.select:
select
ID id,CHANNEL_NAME channelName,CHANNEL_ALIAS_NAME channelAliasName
from hs_channel


        and LANGUAGE_VERSION = #{languageVersion}


        and CHANNEL_ALIAS_NAME = #{channelAliasName}


        and CHANNEL_TYPE = #{channelType}


        and CHANNEL_STATE = #{channelState}


        and IS_DEL = #{isDel}

SELECT
${item}
FROM hs_channel



    ORDER BY #{orderBy}


    ORDER BY CREATE_TIME DESC
