mapper.select:
SELECT ID id, BID bid, MEMBER_ID memberId, FAVORITE_ID favoriteId, FAVORITE_TYPE favoriteType, REMARK remark, CREATE_BY createBy, CREATE_TIME createTime, UPDATE_BY updateBy, UPDATE_TIME updateTime, IS_DEL isDel from hs_member_favorite
AND MEMBER_ID = #{memberId,jdbcType=VARCHAR}
AND FAVORITE_ID = #{favoriteId,jdbcType=INTEGER}
AND FAVORITE_TYPE = #{favoriteType,jdbcType=INTEGER}
AND IS_DEL = #{isDel,jdbcType=INTEGER}
ORDER BY UPDATE_TIME DESC

select

from hs_member_favorite


    AND MEMBER_ID = #{memberId,jdbcType=VARCHAR}


    AND  FAVORITE_ID = #{favoriteId,jdbcType=INTEGER}


    AND FAVORITE_TYPE = #{favoriteType,jdbcType=INTEGER}


    AND IS_DEL = #{isDel,jdbcType=INTEGER}


ORDER BY UPDATE_TIME DESC
