mapper.select:
select
a.ID id, a.AD_FULL_TITLE adFullTitle, a.AD_SHORT_TITLE adShortTitle, a.AD_TYPE adType, a.AD_IMG_URL adImgUrl, a.AD_CONTENT adContent , a.AD_SN adSn, a.AD_C_COUNT adCCount,
a.AD_F_COUNT adFCount, a.AD_DESC adDesc, a.AD_LINK adLink, a.AD_STATE adState, a.IS_DEL isDel
,r.BOOTH_ID boothId
from hs_advert_data a ,hs_booth_advert_rel r

  a.ID = r.ADVERT_ID

    and r.BOOTH_ID in
    #{item}



    and a.IS_DEL = #{isDel}


    and a.AD_STATE = #{adState}

SELECT
${item}
FROM hs_advert_data



    ORDER BY #{orderBy}


    ORDER BY CREATE_TIME DESC
